<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Orbital Calculator</title>
    <style>
        body {
            font-family: 'Courier New', monospace;
            background: linear-gradient(135deg, #0f0f0f, #1a1a2e, #16213e);
            color: #00ffff;
            margin: 0;
            padding: 20px;
            min-height: 100vh;
            display: flex;
            justify-content: center;
            align-items: center;
            overflow: hidden;
        }

        .calculator-container {
            position: relative;
            width: 400px;
            height: 400px;
        }

        .orbit {
            position: absolute;
            border: 2px solid rgba(0, 255, 255, 0.3);
            border-radius: 50%;
            animation: pulse 3s infinite;
        }

        .orbit-1 { width: 200px; height: 200px; top: 100px; left: 100px; }
        .orbit-2 { width: 280px; height: 280px; top: 60px; left: 60px; }
        .orbit-3 { width: 360px; height: 360px; top: 20px; left: 20px; }

        @keyframes pulse {
            0%, 100% { opacity: 0.3; transform: scale(1); }
            50% { opacity: 0.8; transform: scale(1.05); }
        }

        .display {
            position: absolute;
            top: 150px;
            left: 50%;
            transform: translateX(-50%);
            width: 180px;
            height: 60px;
            background: rgba(0, 0, 0, 0.8);
            border: 2px solid #00ffff;
            border-radius: 10px;
            display: flex;
            align-items: center;
            justify-content: center;
            font-size: 24px;
            font-weight: bold;
            text-shadow: 0 0 10px #00ffff;
            z-index: 10;
        }

        .button {
            position: absolute;
            width: 50px;
            height: 50px;
            background: radial-gradient(circle, #00ffff, #0088cc);
            border: none;
            border-radius: 50%;
            color: #000;
            font-size: 18px;
            font-weight: bold;
            cursor: pointer;
            transition: all 0.3s;
            box-shadow: 0 0 15px rgba(0, 255, 255, 0.5);
            z-index: 5;
        }

        .button:hover {
            transform: scale(1.1);
            box-shadow: 0 0 25px rgba(0, 255, 255, 0.8);
        }

        .button:active {
            transform: scale(0.95);
        }

        .operator {
            background: radial-gradient(circle, #ff6b6b, #cc4444);
            box-shadow: 0 0 15px rgba(255, 107, 107, 0.5);
        }

        .operator:hover {
            box-shadow: 0 0 25px rgba(255, 107, 107, 0.8);
        }

        .equals {
            background: radial-gradient(circle, #4ecdc4, #45b7aa);
            box-shadow: 0 0 15px rgba(78, 205, 196, 0.5);
        }

        .equals:hover {
            box-shadow: 0 0 25px rgba(78, 205, 196, 0.8);
        }

        .clear {
            background: radial-gradient(circle, #ffa726, #ff8f00);
            box-shadow: 0 0 15px rgba(255, 167, 38, 0.5);
        }

        .clear:hover {
            box-shadow: 0 0 25px rgba(255, 167, 38, 0.8);
        }

        /* Positioning buttons in orbital pattern */
        .btn-0 { top: 320px; left: 175px; }
        .btn-1 { top: 280px; left: 100px; }
        .btn-2 { top: 280px; left: 175px; }
        .btn-3 { top: 280px; left: 250px; }
        .btn-4 { top: 220px; left: 80px; }
        .btn-5 { top: 220px; left: 175px; }
        .btn-6 { top: 220px; left: 270px; }
        .btn-7 { top: 100px; left: 80px; }
        .btn-8 { top: 80px; left: 175px; }
        .btn-9 { top: 100px; left: 270px; }

        .btn-plus { top: 180px; left: 320px; }
        .btn-minus { top: 240px; left: 320px; }
        .btn-multiply { top: 120px; left: 320px; }
        .btn-divide { top: 60px; left: 320px; }

        .btn-equals { top: 300px; left: 320px; }
        .btn-clear { top: 40px; left: 40px; }
        .btn-decimal { top: 320px; left: 250px; }

        .title {
            position: absolute;
            top: -50px;
            left: 50%;
            transform: translateX(-50%);
            font-size: 28px;
            font-weight: bold;
            text-shadow: 0 0 20px #00ffff;
            animation: glow 2s ease-in-out infinite alternate;
        }

        @keyframes glow {
            from { text-shadow: 0 0 20px #00ffff, 0 0 30px #00ffff; }
            to { text-shadow: 0 0 30px #00ffff, 0 0 40px #00ffff, 0 0 50px #00ffff; }
        }

        .history {
            position: absolute;
            top: 420px;
            left: 50%;
            transform: translateX(-50%);
            width: 300px;
            max-height: 100px;
            overflow-y: auto;
            font-size: 12px;
            opacity: 0.7;
            text-align: center;
        }
    </style>
</head>
<body>
    <div class="calculator-container">
        <div class="title">ORBITAL CALC</div>

        <div class="orbit orbit-1"></div>
        <div class="orbit orbit-2"></div>
        <div class="orbit orbit-3"></div>

        <div class="display" id="display">0</div>

        <!-- Number buttons -->
        <button class="button btn-0" onclick="inputNumber('0')">0</button>
        <button class="button btn-1" onclick="inputNumber('1')">1</button>
        <button class="button btn-2" onclick="inputNumber('2')">2</button>
        <button class="button btn-3" onclick="inputNumber('3')">3</button>
        <button class="button btn-4" onclick="inputNumber('4')">4</button>
        <button class="button btn-5" onclick="inputNumber('5')">5</button>
        <button class="button btn-6" onclick="inputNumber('6')">6</button>
        <button class="button btn-7" onclick="inputNumber('7')">7</button>
        <button class="button btn-8" onclick="inputNumber('8')">8</button>
        <button class="button btn-9" onclick="inputNumber('9')">9</button>

        <!-- Operator buttons -->
        <button class="button operator btn-plus" onclick="inputOperator('+')">+</button>
        <button class="button operator btn-minus" onclick="inputOperator('-')">‚àí</button>
        <button class="button operator btn-multiply" onclick="inputOperator('*')">√ó</button>
        <button class="button operator btn-divide" onclick="inputOperator('/')">/</button>

        <!-- Special buttons -->
        <button class="button equals btn-equals" onclick="calculate()">=</button>
        <button class="button clear btn-clear" onclick="clearDisplay()">C</button>
        <button class="button btn-decimal" onclick="inputDecimal()">.</button>

        <div class="history" id="history"></div>
    </div>

    <script>
        let currentInput = '0';
        let previousInput = null;
        let operator = null;
        let waitingForNewInput = false;
        let history = [];

        const display = document.getElementById('display');
        const historyDiv = document.getElementById('history');

        function updateDisplay() {
            display.textContent = currentInput;
        }

        function updateHistory() {
            historyDiv.innerHTML = history.slice(-3).map(item => `<div>${item}</div>`).join('');
        }

        function inputNumber(num) {
            if (waitingForNewInput) {
                currentInput = num;
                waitingForNewInput = false;
            } else {
                currentInput = currentInput === '0' ? num : currentInput + num;
            }
            updateDisplay();
        }

        function inputOperator(op) {
            if (operator && !waitingForNewInput) {
                calculate();
            }

            previousInput = currentInput;
            operator = op;
            waitingForNewInput = true;

            // Add some visual feedback
            document.querySelector('.orbit-1').style.animation = 'pulse 0.5s';
            setTimeout(() => {
                document.querySelector('.orbit-1').style.animation = 'pulse 3s infinite';
            }, 500);
        }

        function inputDecimal() {
            if (waitingForNewInput) {
                currentInput = '0.';
                waitingForNewInput = false;
            } else if (currentInput.indexOf('.') === -1) {
                currentInput += '.';
            }
            updateDisplay();
        }

        function calculate() {
            if (operator && previousInput !== null && !waitingForNewInput) {
                const prev = parseFloat(previousInput);
                const current = parseFloat(currentInput);
                let result;

                switch (operator) {
                    case '+':
                        result = prev + current;
                        break;
                    case '-':
                        result = prev - current;
                        break;
                    case '*':
                        result = prev * current;
                        break;
                    case '/':
                        result = current !== 0 ? prev / current : 'Error';
                        break;
                    default:
                        return;
                }

                const calculation = `${previousInput} ${operator === '*' ? '√ó' : operator} ${currentInput} = ${result}`;
                history.push(calculation);
                updateHistory();

                currentInput = result.toString();
                operator = null;
                previousInput = null;
                waitingForNewInput = true;

                // Orbital animation on calculation
                document.querySelectorAll('.orbit').forEach((orbit, index) => {
                    orbit.style.animation = `pulse ${0.3 + index * 0.1}s`;
                    setTimeout(() => {
                        orbit.style.animation = 'pulse 3s infinite';
                    }, 1000);
                });
            }
            updateDisplay();
        }

        function clearDisplay() {
            currentInput = '0';
            previousInput = null;
            operator = null;
            waitingForNewInput = false;
            updateDisplay();

            // Clear animation
            document.querySelectorAll('.orbit').forEach(orbit => {
                orbit.style.animation = 'pulse 1s';
                setTimeout(() => {
                    orbit.style.animation = 'pulse 3s infinite';
                }, 1000);
            });
        }

        // Keyboard support
        document.addEventListener('keydown', (event) => {
            const key = event.key;

            if (key >= '0' && key <= '9') {
                inputNumber(key);
            } else if (key === '+' || key === '-' || key === '*' || key === '/') {
                inputOperator(key);
            } else if (key === 'Enter' || key === '=') {
                calculate();
            } else if (key === 'Escape' || key === 'c' || key === 'C') {
                clearDisplay();
            } else if (key === '.') {
                inputDecimal();
            }
        });

        // Initialize
        updateDisplay();
    </script>
</body>
</html>
