<body>
    <div class="calculator">
        <div class="mode-toggle">
            <button class="mode-btn active" id="basicMode">Basic</button>
            <button class="mode-btn" id="scientificMode">Scientific</button>
        </div>

        <div class="display">
            <div class="expression" id="expression"></div>
            <div class="result" id="result">0</div>
        </div>

        <div class="advanced-functions" id="advancedFunctions">
            <button class="btn-function" onclick="addFunction('sin(')">sin</button>
            <button class="btn-function" onclick="addFunction('cos(')">cos</button>
            <button class="btn-function" onclick="addFunction('tan(')">tan</button>
            <button class="btn-function" onclick="addFunction('log(')">log</button>
            <button class="btn-function" onclick="addFunction('ln(')">ln</button>
            <button class="btn-function" onclick="addFunction('‚àö(')">‚àö</button>
            <button class="btn-function" onclick="addOperator('^')">x^y</button>
            <button class="btn-function" onclick="addConstant('œÄ')">œÄ</button>
        </div>

        <div class="buttons">
            <button class="btn-clear" onclick="clearAll()">C</button>
            <button class="btn-clear" onclick="clearEntry()">CE</button>
            <button class="btn-function" onclick="backspace()">‚å´</button>
            <button class="btn-operator" onclick="addOperator('√∑')">√∑</button>

            <button class="btn-number" onclick="addNumber('7')">7</button>
            <button class="btn-number" onclick="addNumber('8')">8</button>
            <button class="btn-number" onclick="addNumber('9')">9</button>
            <button class="btn-operator" onclick="addOperator('√ó')">√ó</button>

            <button class="btn-number" onclick="addNumber('4')">4</button>
            <button class="btn-number" onclick="addNumber('5')">5</button>
            <button class="btn-number" onclick="addNumber('6')">6</button>
            <button class="btn-operator" onclick="addOperator('-')">-</button>

            <button class="btn-number" onclick="addNumber('1')">1</button>
            <button class="btn-number" onclick="addNumber('2')">2</button>
            <button class="btn-number" onclick="addNumber('3')">3</button>
            <button class="btn-operator" onclick="addOperator('+')">+</button>

            <button class="btn-function" onclick="toggleSign()">¬±</button>
            <button class="btn-number" onclick="addNumber('0')">0</button>
            <button class="btn-number" onclick="addNumber('.')">.</button>
            <button class="btn-equals" onclick="calculate()">=</button>
        </div>

        <div class="history" id="history">
            <div style="color: rgba(255,255,255,0.6); font-size: 12px; margin-bottom: 10px;">History</div>
        </div>
    </div>

    <script>
        let currentExpression = '';
        let displayExpression = '';
        let result = '0';
        let history = [];
        let isScientificMode = false;
        let lastResult = null;

        const expressionEl = document.getElementById('expression');
        const resultEl = document.getElementById('result');
        const historyEl = document.getElementById('history');
        const advancedFunctions = document.getElementById('advancedFunctions');

        function updateDisplay() {
            expressionEl.textContent = displayExpression;
            resultEl.textContent = result;
            resultEl.classList.remove('error');
        }

        function addNumber(num) {
            if (result !== '0' && currentExpression === '') {
                currentExpression = result;
                displayExpression = result;
            }

            currentExpression += num;
            displayExpression += num;
            result = currentExpression;
            updateDisplay();
        }

        function addOperator(op) {
            if (currentExpression === '' && lastResult !== null) {
                currentExpression = lastResult.toString();
                displayExpression = lastResult.toString();
            }

            if (currentExpression !== '') {
                // Convert display operators to math operators
                let mathOp = op;
                if (op === '√ó') mathOp = '*';
                if (op === '√∑') mathOp = '/';

                currentExpression += mathOp;
                displayExpression += op;
                result = displayExpression;
                updateDisplay();
            }
        }

        function addFunction(func) {
            currentExpression += func.replace('‚àö', 'Math.sqrt');
            displayExpression += func;
            result = displayExpression;
            updateDisplay();
        }

        function addConstant(constant) {
            let value = constant;
            if (constant === 'œÄ') {
                currentExpression += 'Math.PI';
                displayExpression += 'œÄ';
            }
            result = displayExpression;
            updateDisplay();
        }

        function calculate() {
            if (currentExpression === '') return;

            try {
                // Prepare expression for evaluation
                let evalExpression = currentExpression
                    .replace(/sin\(/g, 'Math.sin(')
                    .replace(/cos\(/g, 'Math.cos(')
                    .replace(/tan\(/g, 'Math.tan(')
                    .replace(/log\(/g, 'Math.log10(')
                    .replace(/ln\(/g, 'Math.log(')
                    .replace(/\^/g, '**')
                    .replace(/‚àö/g, 'Math.sqrt');

                const calculatedResult = eval(evalExpression);

                if (isNaN(calculatedResult) || !isFinite(calculatedResult)) {
                    throw new Error('Invalid calculation');
                }

                // Add to history
                history.unshift({
                    expression: displayExpression,
                    result: calculatedResult
                });

                if (history.length > 10) {
                    history.pop();
                }

                updateHistory();

                result = formatResult(calculatedResult);
                lastResult = calculatedResult;
                currentExpression = '';
                displayExpression = '';

            } catch (error) {
                result = 'Error';
                resultEl.classList.add('error');
                currentExpression = '';
                displayExpression = '';
            }

            updateDisplay();
        }

        function formatResult(num) {
            if (Number.isInteger(num)) {
                return num.toString();
            }

            // Round to 10 decimal places to avoid floating point issues
            const rounded = Math.round(num * 10000000000) / 10000000000;

            // Use exponential notation for very large or small numbers
            if (Math.abs(rounded) >= 1e15 || (Math.abs(rounded) < 1e-6 && rounded !== 0)) {
                return rounded.toExponential(6);
            }

            return rounded.toString();
        }

        function clearAll() {
            currentExpression = '';
            displayExpression = '';
            result = '0';
            lastResult = null;
            updateDisplay();
        }

        function clearEntry() {
            result = '0';
            updateDisplay();
        }

        function backspace() {
            if (currentExpression.length > 0) {
                currentExpression = currentExpression.slice(0, -1);
                displayExpression = displayExpression.slice(0, -1);
                result = displayExpression || '0';
